import React, { Component } from "react";
import { useParams, useNavigate } from 'react-router-dom'
import useFetch from "../Function/useFetch";

class RepoList extends Component{

    constructor (props){
        super(props)
        this.state = {
            UserName : this.props.UserName,
            CurrentIndex : 0,
            CurrentRepoList: [GetRepoListData(this.props.UserName, 0)]
        }

    }
    handleClick(){
        this.setState(( state ) => ({
            CurrentIndex : state.CurrentIndex + 10,
            CurrentRepoList : state.ConcatRepoListData.concat( GetRepoListData( state.UserName, state.CurrentIndex ) )
        }));
    }
    render(){
        console.log("render")
        return (
            <div>
                <h1>
                    Repository for { this.state.UserName }
                </h1>
                <CreateRepoList UserName = {this.state.UserName} repo = { this.state.CurrentRepoList}/>
                <div onClick={ () => this.handleClick() }>REFETCH</div>
            </div>
        ) ;
    }
}
//<RepoListData UserName = {this.state.UserName} CurrentIndex = {this.state.CurrentIndex}/>
const RepoListData = React.memo(GetRepoListData, areEqual);
function areEqual( prevProps, nextProps ){

    return prevProps.CurrentIndex === nextProps.CurrentIndex
}
function GetRepoListData (UserName, CurrentIndex ){

    let url = "https://api.github.com/users/"+UserName+"/repos"  
    let {data : repo, loading, error} = useFetch( url, CurrentIndex ) 

    if( loading ) return (<div>LOADING....</div>)
    if( error ) console.log(error)

    return repo
    /*return (
        <div>
            <h1>
                Repository for { props.UserName }
            </h1>
            <div>
                {
                    repo && repo.map((data) => (
                        <div onClick={()=>{navigate('/users/'+props.UserName+'/repos/'+data.name)}}> 
                            <p>name: {data.name}</p>
                            <p>stargazer: {data.stargazers_count}</p>
                        </div>
                    ))
                }
            </div>
        </div>
    );*/
}

function CreateRepoList(props){

    let navigate = useNavigate()
    return(
        <div>
            {
                props.repo && props.repo.map((data) => (
                    <div onClick={()=>{navigate('/users/'+props.UserName+'/repos/'+data.name)}}> 
                        <p>name: {data.name}</p>
                        <p>stargazer: {data.stargazers_count}</p>
                    </div>
                ))
            }
        </div>
    );
}
function SetInitInfo(){
    let { username } : {username : String} = useParams();

    return ( <RepoList UserName = { username }/> );
}

export default SetInitInfo;
