import React, { useState } from 'react'

function useFetch( url, currentIndex ) {

    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)

    React.useEffect(() => {
        console.log(url)
        setLoading(true)
        fetch(url, { method: "GET" })
        .then((response) => response.json())
        .then( data => {
            let LoadingData = []
            for (let index = 0; index < data.length || index < 10; index++) {
                LoadingData.push(data[index])
            }
            setData(LoadingData)
        })
        .catch((error) => {
            setError(error)
        }).finally(()=> setLoading(false));
    },[ url ])

    const ReFetch = (()=>{
        setLoading(true)
        fetch(url, { method: "GET" })
        .then((response) => response.json())
        .then( data => {
            let LoadingData = []
            for (let index = currentIndex; index < data.length || index < 10; index++) {
                LoadingData.push(data[index])
            }
            setData(LoadingData)
        })
        .catch((error) => {
            setError(error)
        }).finally(()=> setLoading(false));
    })
    return {data, loading, error}
}

export default useFetch
