import React, { useState } from 'react'

function useFetch( url, currentIndex = 0 ) {

    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)

    React.useEffect(() => {
        console.log(url)
        setLoading(true)
        fetch(url, { method: "GET" })
        .then((response) => response.json())
        .then( datas => {
            let LoadingData = []
            console.log(datas.length)
            for (let index = 0; index < datas.length || index < 10; index++) {
                LoadingData.push(datas[index])
            }
            setData(LoadingData)
        })
        .catch((error) => {
            setError(error)
        }).finally(()=> setLoading(false));
    },[ url ])
    return {data, loading, error, currentIndex}
}

export default useFetch
