import React, { Component } from "react";
import { useParams, useNavigate } from 'react-router-dom'
import useFetch from "../Function/useFetch";

function GetRepoListData( username, nextIndex ){

    let url = "https://api.github.com/users/"+username+"/repos"

    let {data : repo, loading, error, currentIndex} = useFetch( url, nextIndex )

    if( loading ) return (<div>LOADING....</div>)
    if( error ) console.log(error)

    return repo
}

class RepoList extends Component{

    constructor(props){
        super(props)
        this.state = {
            username : "",
            repoList : [],
            index : 0
        }
    }

    reFetch( ) {
        let repo = GetRepoListData( this.state.username, this.state.index)

        this.setState({
            repoList : this.state.repoList.concat(repo),
            index : this.state.index + 10
        })
    }
    render(){

        return (
            <div>
                <h1>
                    Repository for { this.state.username }
                </h1>
                <div>
                    {
                        this.state.repoList && this.state.repoList.map((data) => (
                            <div onClick={()=>{navigate('/users/'+this.state.username+'/repos/'+data.name)}}> 
                                <p>name: {data.name}</p>
                                <p>stargazer: {data.stargazers_count}</p>
                            </div>
                        ))
                    }
                </div>
            </div>


        );
    }

}


export default RepoList;
