import React, { Component } from "react";
import { useParams, useNavigate } from 'react-router-dom'
import useFetch from "../Function/useFetch";

class RepoList extends Component{

    constructor (props){
        super(props)
        this.state = {
            UserName : this.props.UserName,
            CurrentIndex : 0
        }
        this.handleChange = this.handleChange.bind(this)
    }
    ReFetchForGettingMoreData(){
        this.setState({
            CurrentIndex : this.state.CurrentIndex + 10
        });
    }
    render(){
        return (
            <div>
                <GetRepoListData UserName = {this.state.UserName} CurrentIndex = {this.state.CurrentIndex}/>
                <div onClick={ this.ReFetchForGettingMoreData }>REFETCH</div>
            </div>

        ) ;
    }
}

function GetRepoListData (props){

    let navigate = useNavigate()
    let url = "https://api.github.com/users/"+props.UserName+"/repos"  
    let {data : repo, loading, error} = useFetch( url, props.CurrentIndex )

    if( loading ) return (<div>LOADING....</div>)
    if( error ) console.log(error)
    console.log(repo)
    return (
        <div>
            <h1>
                Repository for { props.UserName }
            </h1>
            <div>
                {
                    repo && repo.map((data) => (
                        <div onClick={()=>{navigate('/users/'+props.UserName+'/repos/'+data.name)}}> 
                            <p>name: {data.name}</p>
                            <p>stargazer: {data.stargazers_count}</p>
                        </div>
                    ))
                }
            </div>
        </div>
    );
}

function SetInitInfo(){
    let { username } : {username : String} = useParams();

    return ( <RepoList UserName = { username }/> );
}

export default SetInitInfo;
