import React, { useState } from "react";
import { useParams, useNavigate } from 'react-router-dom'
import useFetch from "../Function/useFetch";
function RepoListData ( ){

    let { username } : {username : String} = useParams()
    let [ CurrentIndex, setCurrentIndex] = useState(0)
    let [ repoList, setRepoList] = useState([])
    let [ url, setUrl] = useState("https://api.github.com/users/"+username+"/repos")
    let navigate = useNavigate()
    //let url = "https://api.github.com/users/"+username+"/repos"  

    React.useEffect(()=>{
        let {data : repo, loading, error} = useFetch( url, CurrentIndex )
    }, [url, CurrentIndex ])


    React.useEffect(()=>{
        if( !loading ){
            setRepoList(repoList.concat( repo ))
            setCurrentIndex( CurrentIndex + 10 )
        }
    })
    if( loading ) return (<div>LOADING....</div>)
    if( error ) console.log(error)

    return (
        <div>
            <h1>
                Repository for { username }
            </h1>
            <div>
                {
                    repoList && repoList.map((data) => (
                        <div onClick={()=>{navigate('/users/'+username+'/repos/'+data.name)}}> 
                            <p>name: {data.name}</p>
                            <p>stargazer: {data.stargazers_count}</p>
                        </div>
                    ))
                }
            </div>
        </div>
    );
}

export default RepoListData;
