import React from "react";
import { useParams, useNavigate } from 'react-router-dom'
import useFetch from "../Function/useFetch";

function ReposInfo (){
    let { username } : {username : String} = useParams()
    let navigate = useNavigate()
    let url = "https://api.github.com/users/"+username+"/repos"

    let {data : repo, loading, error} = useFetch( url )

    if( loading ) return (<div>LOADING....</div>)
    if( error ) console.log(error)
    console.log(repo[0]?.name)
    return (
        <div>
            <h1>
                Repository for { username }
            </h1>
            <div>
                {
                    repo && repo.map((data) => (
                            <div onClick={()=>{navigate('/users/'+username+'/repos/'+data.name)}}> 
                                <p>name: {data.name}</p>
                                <p>stargazer: {data.stargazers_count}</p>
                            </div>

                        )
                    )
                }
            </div>
        </div>


    );
}

export default ReposInfo;
