import React, { useState, useRef } from "react";
import { useParams, useNavigate } from 'react-router-dom'
import useFetch from "../Function/useFetch";

function IsBottom ( listInnerRef ){
    if (listInnerRef.current) {
        const { scrollTop, scrollHeight, clientHeight } = listInnerRef.current;
        console.log("hi")
        return (scrollTop + clientHeight === scrollHeight)
    }
}
function RepoList (){

    const listInnerRef = useRef();
    let [ Refetch, setRefetch] = useState(false);

    const IsBottom  = () => {
        if (listInnerRef.current) {
            const { scrollTop, scrollHeight, clientHeight } = listInnerRef.current;
            if(scrollTop + clientHeight === scrollHeight){
                console.log("bottom")
                setRefetch(true)
            }
        }
    }
    let [ currentIndex, setIndex] = useState(10)
    let { username } : {username : String} = useParams()
    let navigate = useNavigate()
    let url = "https://api.github.com/users/"+username+"/repos"

    let {data : repo, loading, error} = useFetch( url, currentIndex )
    console.log(Refetch)
    React.useEffect(()=>{
        console.log("loading = "+loading)
        console.log("error = "+error)
        if( !loading && !error && Refetch){
            setIndex( currentIndex + 10 )
        }
    }, [])
    if( loading ) return (<div>LOADING....</div>)
    if( error ) console.log(error)


    return (
        <div onScroll={IsBottom} ref={listInnerRef} style={{ height: "500px", overflowY: "auto" }}>
            <h1>
                Repository for { username }
            </h1>
            <div >
                {
                    repo && repo.map((data) => (
                        <div onClick={()=>{navigate('/users/'+username+'/repos/'+data.name)}}> 
                            <p>name: {data.name}</p>
                            <p>stargazer: {data.stargazers_count}</p>
                        </div>
                    ))
                }
            </div>
        </div>


    );
}

export default RepoList;
